import React, { useState } from "react";
import Loader from "./Loader.js";
import "./Responsive.css";
import "./Weather.css";
import "./Header.css";
import "./Weather.css";
import axios from "axios";

export default function Weather() {
  const [ready, setReady] = useState(false);
  const [weatherData, setWeatherData] = useState({});

  function handleResponse(response) {
    setReady(true);
    setWeatherData({
      temperature: Math.round(response.data.main.temp),
      description: response.data.weather[0],
      imgUrl: `http://openweathermap.org/img/wn/01d@2x.png`,
      humidity: response.data.main.humidity,
      maxTemp: 22,
      minTemp: 12,
    });
  }

  if (ready) {
    return (
      <div className="wrapper">
        <h1>Toronto</h1>
        <section className="units">
          <div>
            <button className="btn btn-primary ">C째</button>
          </div>
          <div>
            <button className="btn btn-primary ">F</button>
          </div>
        </section>
        <hr />
        <h2 className="text-center">4 Nov, 2022</h2>
        <hr />
        <div className="inner-info">
          <div className="row">
            <div className="col-6">
              <h1>{weatherData.temperature}째C</h1>
            </div>
            <div className="col-6 icon-main">
              <img src={weatherData.imgUrl} alt="icon-weather" width="120" />
            </div>
          </div>
          <div className="row">
            <div className="col-6">
              <ul>
                <li>{weatherData.description}</li>
                <li>Humidity: {weatherData.humidity} %</li>
              </ul>
            </div>
            <div className="col-6 right-section">
              <ul>
                <li>Max temp {weatherData.maxTemp} 째C</li>
                <li>Min temp {weatherData.minTemp} 째C</li>
              </ul>
            </div>
          </div>
          <hr />
          <div></div>
        </div>
      </div>
    );
  } else {
    let key = "dfe1cfa2526cae5b947abe89b8666636";
    let apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=toronto&appid=${key}&units=metric`;
    axios.get(apiUrl).then(handleResponse);
    return (
      <div>
        <Loader />
      </div>
    );
  }
}
